pragma solidity ^0.6.6;

import {ChildERC20} from "../child/ChildERC20.sol";

contract BetteryToken is ChildERC20 {
    constructor(
        string memory _name,
        string memory _symbol,
        uint8 _decimals,
        uint256 _initialSupplyCoins
        ) public ChildERC20(_name, _symbol, _decimals) {
            _mint(msg.sender, _initialSupplyCoins * (10**uint256(_decimals)));
        }
}
